import React, { useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import { ThreeScene } from '../utils/ThreeScene';
import './Home.css';

function Home() {
  const containerRef = useRef(null);
  const sceneRef = useRef(null);

  useEffect(() => {
    if (containerRef.current && !sceneRef.current) {
      sceneRef.current = new ThreeScene(containerRef.current);
      sceneRef.current.createParticles(150);
      sceneRef.current.createFloatingMesh();
    }

    return () => {
      if (sceneRef.current) {
        sceneRef.current.dispose();
        sceneRef.current = null;
      }
    };
  }, []);

  return (
    <div className="home">
      <div className="hero-canvas" ref={containerRef}></div>

      <div className="hero-content">
        <div className="hero-text">
          <h1 className="hero-headline">
            See Risk<br />
            <span className="gradient-text">Before It Emerges</span>
          </h1>

          <p className="hero-subheading">
            A living system for understanding urban futures.
          </p>

          <Link to="/platform" className="hero-cta">
            Explore the Platform
          </Link>
        </div>

        <div className="scroll-indicator">
          <div className="scroll-dot"></div>
          <p>Scroll to discover</p>
        </div>
      </div>

      <section className="vision-section">
        <div className="vision-container">
          <div className="vision-card">
            <h2>See what's coming.</h2>
            <p>Real-time environmental intelligence reveals emerging risks before they cascade.</p>
          </div>

          <div className="vision-card">
            <h2>Simulate outcomes.</h2>
            <p>Model interventions. Understand impact. Make decisions with clarity.</p>
          </div>

          <div className="vision-card">
            <h2>Act with confidence.</h2>
            <p>Backed by data. Guided by intelligence. Designed for cities that lead.</p>
          </div>
        </div>
      </section>

      <section className="cta-section">
        <div className="cta-content">
          <h2>Enter the System</h2>
          <div className="cta-links">
            <Link to="/platform" className="cta-link">Platform</Link>
            <Link to="/scenarios" className="cta-link">Scenarios</Link>
            <Link to="/impact" className="cta-link">Impact</Link>
            <Link to="/about" className="cta-link">Philosophy</Link>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Home;
