import React from 'react';
import './ScenarioButtons.css';

function ScenarioButtons({ onScenario }) {
  const scenarios = [
    { id: 'normal', label: 'Normal', icon: '‚òÄÔ∏è' },
    { id: 'heatwave', label: 'Heatwave', icon: 'üî•' },
    { id: 'drought', label: 'Drought', icon: 'üèúÔ∏è' },
    { id: 'crisis', label: 'Crisis', icon: '‚ö†Ô∏è' }
  ];

  return (
    <div className="scenario-buttons">
      {scenarios.map((scenario) => (
        <button
          key={scenario.id}
          className="scenario-btn"
          onClick={() => onScenario(scenario.id)}
        >
          <span className="scenario-icon">{scenario.icon}</span>
          <span className="scenario-label">{scenario.label}</span>
        </button>
      ))}
    </div>
  );
}

export default ScenarioButtons;
