import * as THREE from 'three';

export class ThreeScene {
  constructor(container) {
    this.container = container;
    this.scene = new THREE.Scene();
    this.camera = new THREE.PerspectiveCamera(
      75,
      container.clientWidth / container.clientHeight,
      0.1,
      1000
    );
    this.renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    this.renderer.setSize(container.clientWidth, container.clientHeight);
    this.renderer.setClearColor(0x000000, 0);
    this.renderer.setPixelRatio(window.devicePixelRatio);
    container.appendChild(this.renderer.domElement);

    this.camera.position.z = 5;
    this.particles = [];
    this.mouse = { x: 0, y: 0 };
    this.targetRotation = { x: 0, y: 0 };

    this.setupLights();
    this.setupEventListeners();
    this.animate();
  }

  setupLights() {
    const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
    this.scene.add(ambientLight);

    const pointLight = new THREE.PointLight(0x60a5fa, 1);
    pointLight.position.set(5, 5, 5);
    this.scene.add(pointLight);

    const pointLight2 = new THREE.PointLight(0x06b6d4, 0.8);
    pointLight2.position.set(-5, -5, 5);
    this.scene.add(pointLight2);
  }

  setupEventListeners() {
    window.addEventListener('mousemove', (e) => {
      this.mouse.x = (e.clientX / window.innerWidth) * 2 - 1;
      this.mouse.y = -(e.clientY / window.innerHeight) * 2 + 1;
    });

    window.addEventListener('resize', () => this.onWindowResize());
  }

  onWindowResize() {
    const width = this.container.clientWidth;
    const height = this.container.clientHeight;
    this.camera.aspect = width / height;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(width, height);
  }

  createParticles(count = 100) {
    const geometry = new THREE.BufferGeometry();
    const positions = new Float32Array(count * 3);

    for (let i = 0; i < count * 3; i += 3) {
      positions[i] = (Math.random() - 0.5) * 20;
      positions[i + 1] = (Math.random() - 0.5) * 20;
      positions[i + 2] = (Math.random() - 0.5) * 20;
    }

    geometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

    const material = new THREE.PointsMaterial({
      color: 0x60a5fa,
      size: 0.1,
      sizeAttenuation: true,
      transparent: true,
      opacity: 0.6
    });

    const particles = new THREE.Points(geometry, material);
    this.scene.add(particles);
    this.particles.push(particles);

    return particles;
  }

  createFloatingMesh() {
    const geometry = new THREE.IcosahedronGeometry(2, 4);
    const material = new THREE.MeshPhongMaterial({
      color: 0x3b82f6,
      emissive: 0x1e40af,
      wireframe: false,
      transparent: true,
      opacity: 0.8
    });

    const mesh = new THREE.Mesh(geometry, material);
    this.scene.add(mesh);
    return mesh;
  }

  createWireframeGeometry() {
    const geometry = new THREE.TorusGeometry(3, 1, 16, 100);
    const material = new THREE.MeshPhongMaterial({
      color: 0x06b6d4,
      emissive: 0x0891b2,
      wireframe: true,
      transparent: true,
      opacity: 0.5
    });

    const mesh = new THREE.Mesh(geometry, material);
    this.scene.add(mesh);
    return mesh;
  }

  animate() {
    requestAnimationFrame(() => this.animate());

    // Smooth camera movement based on mouse
    this.targetRotation.x += (this.mouse.y * 0.5 - this.targetRotation.x) * 0.05;
    this.targetRotation.y += (this.mouse.x * 0.5 - this.targetRotation.y) * 0.05;

    this.camera.rotation.order = 'YXZ';
    this.camera.rotation.y = this.targetRotation.y;
    this.camera.rotation.x = this.targetRotation.x;

    // Animate particles
    this.particles.forEach((particle) => {
      particle.rotation.x += 0.0001;
      particle.rotation.y += 0.0002;
    });

    // Animate meshes
    this.scene.children.forEach((child) => {
      if (child instanceof THREE.Mesh && child !== this.camera) {
        child.rotation.x += 0.0005;
        child.rotation.y += 0.0008;
        child.position.y += Math.sin(Date.now() * 0.0005) * 0.001;
      }
    });

    this.renderer.render(this.scene, this.camera);
  }

  dispose() {
    this.renderer.dispose();
    this.container.removeChild(this.renderer.domElement);
  }
}
