import React from 'react';
import './RiskCards.css';

function RiskCards({ risks }) {
  const getRiskColor = (level) => {
    switch (level) {
      case 'low':
        return '#10b981';
      case 'medium':
        return '#f59e0b';
      case 'high':
        return '#ef4444';
      default:
        return '#6b7280';
    }
  };

  const cards = [
    {
      title: 'Environmental Risk',
      data: risks.environmental_risk,
      icon: 'üåç'
    },
    {
      title: 'Health Risk',
      data: risks.health_risk,
      icon: 'üè•'
    },
    {
      title: 'Food Security Risk',
      data: risks.food_security_risk,
      icon: 'üåæ'
    }
  ];

  return (
    <div className="risk-cards">
      {cards.map((card, idx) => (
        <div key={idx} className="risk-card">
          <div className="risk-card-header">
            <span className="risk-icon">{card.icon}</span>
            <h3>{card.title}</h3>
          </div>
          <div className="risk-card-content">
            <div className="risk-level" style={{ color: getRiskColor(card.data.level) }}>
              {card.data.level.toUpperCase()}
            </div>
            <div className="risk-probability">
              <span className="label">Probability</span>
              <span className="value">{card.data.probability}%</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default RiskCards;
