import React, { useState, useEffect } from 'react';
import './App.css';
import CitySelector from './components/CitySelector';
import RiskCards from './components/RiskCards';
import MetricsDisplay from './components/MetricsDisplay';
import ScenarioButtons from './components/ScenarioButtons';
import CustomSliders from './components/CustomSliders';
import ComparisonDisplay from './components/ComparisonDisplay';
import EconomicImpact from './components/EconomicImpact';
import HistoricalCharts from './components/HistoricalCharts';
import RecommendationsList from './components/RecommendationsList';

function App() {
  const [selectedCity, setSelectedCity] = useState(1);
  const [currentState, setCurrentState] = useState(null);
  const [risks, setRisks] = useState(null);
  const [historical, setHistorical] = useState(null);
  const [comparison, setComparison] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sliderValues, setSliderValues] = useState({
    aqi: 150,
    hospital_load: 50,
    crop_supply: 70,
    temperature: 30
  });

  // Fetch current state and risks
  useEffect(() => {
    fetchData();
  }, [selectedCity]);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    try {
      const [stateRes, risksRes, historicalRes] = await Promise.all([
        fetch(`/api/v1/current-state?city_id=${selectedCity}`),
        fetch(`/api/v1/risk-assessment?city_id=${selectedCity}`),
        fetch(`/api/v1/historical?city_id=${selectedCity}&hours=24`)
      ]);

      if (!stateRes.ok || !risksRes.ok || !historicalRes.ok) {
        throw new Error('Failed to fetch data');
      }

      const stateData = await stateRes.json();
      const risksData = await risksRes.json();
      const historicalData = await historicalRes.json();

      setCurrentState(stateData);
      setRisks(risksData);
      setHistorical(historicalData);
      setSliderValues({
        aqi: stateData.aqi,
        hospital_load: stateData.hospital_load,
        crop_supply: stateData.crop_supply,
        temperature: parseFloat(stateData.temperature)
      });
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleScenario = async (scenario) => {
    setLoading(true);
    setError(null);
    try {
      const scenarioMap = {
        normal: { aqi: 100, hospital_load: 30, crop_supply: 85, temperature: 25 },
        heatwave: { aqi: 400, hospital_load: 85, crop_supply: 40, temperature: 45 },
        drought: { aqi: 250, hospital_load: 60, crop_supply: 20, temperature: 38 },
        crisis: { aqi: 450, hospital_load: 95, crop_supply: 10, temperature: 48 }
      };

      const res = await fetch('/api/v1/scenario', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(scenarioMap[scenario])
      });

      if (!res.ok) throw new Error('Failed to calculate scenario');

      const data = await res.json();
      setComparison(data);
      setSliderValues(scenarioMap[scenario]);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleCalculateImpact = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch('/api/v1/scenario', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(sliderValues)
      });

      if (!res.ok) throw new Error('Failed to calculate impact');

      const data = await res.json();
      setComparison(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <header className="header">
        <div className="header-content">
          <h1>Urban Risk Assessment Dashboard</h1>
          <p>Real-time environmental, health, and food security monitoring</p>
        </div>
      </header>

      {error && <div className="error-banner">{error}</div>}
      {loading && <div className="loading-banner">Loading...</div>}

      <main className="container">
        <CitySelector selectedCity={selectedCity} onCityChange={setSelectedCity} />

        {currentState && risks && (
          <>
            <RiskCards risks={risks} />
            <MetricsDisplay metrics={currentState} />

            <section className="scenarios-section">
              <h2>Scenario Analysis</h2>
              <ScenarioButtons onScenario={handleScenario} />
            </section>

            <section className="sliders-section">
              <h2>Custom Scenario</h2>
              <CustomSliders
                values={sliderValues}
                onChange={setSliderValues}
                onCalculate={handleCalculateImpact}
              />
            </section>

            {comparison && (
              <>
                <ComparisonDisplay comparison={comparison} />
                <EconomicImpact impact={comparison.economic_impact} />
              </>
            )}

            {historical && <HistoricalCharts data={historical} />}

            <RecommendationsList risks={risks} />
          </>
        )}
      </main>
    </div>
  );
}

export default App;
