import React, { useEffect, useRef } from 'react';
import { ThreeScene } from '../utils/ThreeScene';
import './Platform.css';

function Platform() {
  const containerRef = useRef(null);
  const sceneRef = useRef(null);

  useEffect(() => {
    if (containerRef.current && !sceneRef.current) {
      sceneRef.current = new ThreeScene(containerRef.current);
      sceneRef.current.createParticles(100);
      sceneRef.current.createWireframeGeometry();
    }

    return () => {
      if (sceneRef.current) {
        sceneRef.current.dispose();
        sceneRef.current = null;
      }
    };
  }, []);

  const capabilities = [
    {
      title: 'Real-Time Monitoring',
      description: 'Environmental, health, and food security risks tracked as they unfold.'
    },
    {
      title: 'Scenario Simulation',
      description: 'Model interventions. Understand cascading outcomes with precision.'
    },
    {
      title: 'Economic Clarity',
      description: 'See costs, savings, and ROI. Make the business case for action.'
    },
    {
      title: 'Actionable Intelligence',
      description: 'Recommendations tailored to your city\'s unique risk profile.'
    }
  ];

  return (
    <div className="platform">
      <div className="platform-hero">
        <div className="platform-canvas" ref={containerRef}></div>
        <div className="platform-hero-content">
          <h1>The Platform</h1>
          <p>A system designed for clarity and action.</p>
        </div>
      </div>

      <section className="capabilities-section">
        <div className="capabilities-container">
          <div className="section-header">
            <h2>Core Capabilities</h2>
            <p>What the system reveals</p>
          </div>

          <div className="capabilities-grid">
            {capabilities.map((cap, idx) => (
              <div key={idx} className="capability-card">
                <div className="capability-number">{String(idx + 1).padStart(2, '0')}</div>
                <h3>{cap.title}</h3>
                <p>{cap.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      <section className="features-section">
        <div className="features-container">
          <div className="feature-item">
            <div className="feature-icon">◆</div>
            <h3>Multi-City Intelligence</h3>
            <p>Monitor environmental, health, and food security risks across multiple urban centers simultaneously.</p>
          </div>

          <div className="feature-item">
            <div className="feature-icon">◆</div>
            <h3>Predictive Modeling</h3>
            <p>Simulate scenarios and understand how interventions cascade through interconnected systems.</p>
          </div>

          <div className="feature-item">
            <div className="feature-icon">◆</div>
            <h3>Decision Support</h3>
            <p>Get recommendations grounded in data, designed for leaders who need clarity.</p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Platform;
